import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Courses = () => {
  const [courses, setCourses] = useState([]);
  const [editingCourse, setEditingCourse] = useState(null);
  const [form, setForm] = useState({ title: '', description: '' });

  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');
  const userId = localStorage.getItem('userId'); // Save this on login from token

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/courses');
      setCourses(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleEnroll = async (courseId) => {
    try {
      await axios.post(
        `http://localhost:5000/api/courses/${courseId}/enroll`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Enrolled successfully!');
    } catch (err) {
      alert(err.response?.data?.message || 'Enrollment failed');
    }
  };

  const handleEdit = (course) => {
    setEditingCourse(course._id);
    setForm({ title: course.title, description: course.description });
  };

  const handleUpdate = async () => {
    try {
      await axios.put(
        `http://localhost:5000/api/courses/${editingCourse}`,
        form,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Course updated!');
      setEditingCourse(null);
      fetchCourses();
    } catch (err) {
      alert('Failed to update course');
    }
  };

  const handleDelete = async (courseId) => {
    try {
      await axios.delete(`http://localhost:5000/api/courses/${courseId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Course deleted');
      fetchCourses();
    } catch (err) {
      alert('Failed to delete course');
    }
  };

  return (
    <div className="container">
      <h2>Available Courses</h2>
      {courses.length === 0 ? (
        <p>No courses available</p>
      ) : (
        <ul>
          {courses.map((course) => (
            <li key={course._id} style={{ marginBottom: '2rem', borderBottom: '1px solid #ccc', paddingBottom: '1rem' }}>
              {editingCourse === course._id ? (
                <div>
                  <input
                    type="text"
                    value={form.title}
                    onChange={(e) => setForm({ ...form, title: e.target.value })}
                    placeholder="Title"
                    style={{ display: 'block', marginBottom: '0.5rem' }}
                  />
                  <textarea
                    value={form.description}
                    onChange={(e) => setForm({ ...form, description: e.target.value })}
                    placeholder="Description"
                    style={{ display: 'block', marginBottom: '0.5rem', width: '100%' }}
                  />
                  <button onClick={handleUpdate}>Save</button>
                  <button onClick={() => setEditingCourse(null)} style={{ marginLeft: '0.5rem' }}>Cancel</button>
                </div>
              ) : (
                <>
                  <h3>{course.title}</h3>
                  <p>{course.description}</p>

                  {role === 'student' && (
                    <button onClick={() => handleEnroll(course._id)}>Enroll</button>
                  )}

                  {role === 'instructor' && course.instructor === userId && (
                    <>
                      <button onClick={() => handleEdit(course)}>Edit</button>
                      <button onClick={() => handleDelete(course._id)} style={{ marginLeft: '0.5rem' }}>Delete</button>
                    </>
                  )}
                </>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Courses;
