import React, { useState } from 'react';
import axios from 'axios';

function GptSuggest() {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!prompt.trim()) return;

    try {
      setLoading(true);
      setResponse('');
      setError('');

      const token = localStorage.getItem('token');
      const res = await axios.post(
        'http://localhost:5000/api/gpt/recommend',
        { prompt },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      setResponse(res.data.response);
    } catch (err) {
      setError(err.response?.data?.message || 'Error getting response');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>ðŸŽ“ Course Recommendation (GPT)</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <textarea
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="E.g. I want to become a mobile app developer"
          style={styles.textarea}
          rows={4}
        />
        <button type="submit" style={styles.button} disabled={loading}>
          {loading ? 'Getting Suggestions...' : 'Get Recommendations'}
        </button>
      </form>

      {error && <p style={styles.error}>{error}</p>}
      {response && (
        <div style={styles.responseBox}>
          <h4>ðŸ“‹ Suggested Courses:</h4>
          <pre>{response}</pre>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '600px',
    margin: '30px auto',
    padding: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    fontFamily: 'Arial',
  },
  title: {
    textAlign: 'center',
  },
  form: {
    marginTop: '20px',
  },
  textarea: {
    width: '100%',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '6px',
    border: '1px solid #ccc',
    resize: 'none',
  },
  button: {
    marginTop: '10px',
    padding: '10px 20px',
    backgroundColor: '#1e88e5',
    color: '#fff',
    fontSize: '16px',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
  },
  error: {
    color: 'red',
    marginTop: '10px',
  },
  responseBox: {
    marginTop: '20px',
    backgroundColor: '#eef2f3',
    padding: '15px',
    borderRadius: '8px',
    whiteSpace: 'pre-wrap',
  },
};

export default GptSuggest;
