import React, { useState } from 'react';
import axios from 'axios';

const GPTRecommendations = () => {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post('http://localhost:5000/api/gpt/recommend', { prompt }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setResponse(res.data.message);
    } catch (err) {
      alert('Failed to get recommendation');
      console.error(err);
    }
  };

  return (
    <div>
      <h2>Course Recommendations (AI)</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          placeholder="E.g., I want to be a software engineer, what courses should I follow?"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          rows={4}
          cols={50}
        />
        <br />
        <button type="submit">Ask AI</button>
      </form>
      {response && (
        <div>
          <h4>Suggested Courses:</h4>
          <p>{response}</p>
        </div>
      )}
    </div>
  );
};

export default GPTRecommendations;
